def centrar(root, ancho, alto):    # funcion para que todas las ventanas se puedan abrir en en centro de la pantalla    # r: es la ventana raiz    # ancho: dimension horizontal de la ventana    # alto: dimension vertical de la ventana    altura_pantalla = root.winfo_screenheight()    anchura_pantalla = root.winfo_screenwidth()    x = (anchura_pantalla // 2) - (ancho // 2)    y = (altura_pantalla // 2) - (alto // 2)    root.geometry(f"+{x}+{y}")